// main.c\n#include <stdio.h>\n#include <dlfcn.h>\n\nvoid main_function() {\n    printf("Hello from main function!\n");\n}\n\nint main() {\n    void *handle;\n    void (*library_function)();\n\n    // Open the shared library\n    handle = dlopen("./libexample.so", RTLD_LAZY);\n    if (!handle) {\n        fprintf(stderr, "%s\n", dlerror());\n        return 1;\n    }\n\n    // Lookup the library function\n    *(void **) (&library_function) = dlsym(handle, "library_function");\n    if (!library_function) {\n        fprintf(stderr, "%s\n", dlerror());\n        dlclose(handle);\n        return 1;\n    }\n\n    // Call the library function\n    library_function();\n\n    // Close the library\n    dlclose(handle);\n    return 0;\n}