// writer.c\n   #include <stdio.h>\n   #include <stdlib.h>\n   #include <fcntl.h>\n   #include <unistd.h>\n   #include <string.h>\n\n   int main(int argc, char *argv[]) {\n       int fd;\n       char *fifo = "/tmp/fifo";\n       char message[100];\n\n       if (argc != 2) {\n           fprintf(stderr, "Usage: %s <message>\n", argv[0]);\n           exit(EXIT_FAILURE);\n       }\n\n       strncpy(message, argv[1], sizeof(message) - 1);\n       message[sizeof(message) - 1] = '\0';\n\n       fd = open(fifo, O_WRONLY);\n       if (fd == -1) {\n           perror("open");\n           exit(EXIT_FAILURE);\n       }\n\n       if (write(fd, message, strlen(message)) == -1) {\n           perror("write");\n           close(fd);\n           exit(EXIT_FAILURE);\n       }\n\n       close(fd);\n       return 0;\n   }\n\n   // reader.c\n   #include <stdio.h>\n   #include <stdlib.h>\n   #include <fcntl.h>\n   #include <unistd.h>\n\n   int main() {\n       int fd;\n       char *fifo = "/tmp/fifo";\n       char buffer[4096];\n       ssize_t bytes_read;\n\n       fd = open(fifo, O_RDONLY);\n       if (fd == -1) {\n           perror("open");\n           exit(EXIT_FAILURE);\n       }\n\n       while ((bytes_read = read(fd, buffer, sizeof(buffer) - 1)) > 0) {\n           buffer[bytes_read] = '\0'; // Null-terminate the read bytes\n           printf("Read: %s\n", buffer);\n       }\n\n       close(fd);\n       return 0;\n   }