#define _GNU_SOURCE\n#include <sys/types.h>\n#include <sys/wait.h>\n#include <fcntl.h>\n#include <unistd.h>\n#include <stdio.h>\n#include <stdlib.h>\n\nint main() {\n    pid_t pid = <TARGET_PID>; // Replace <TARGET_PID> with the PID of the non-child process\n\n    // Obtain a file descriptor for the target process\n    int pidfd = pidfd_open(pid, 0);\n    if (pidfd == -1) {\n        perror("pidfd_open");\n        exit(EXIT_FAILURE);\n    }\n\n    // Wait for the target process to exit\n    struct pollfd pfd = {\n        .fd = pidfd,\n        .events = POLLIN,\n    };\n\n    int ret = poll(&pfd, 1, -1);\n    if (ret == -1) {\n        perror("poll");\n        close(pidfd);\n        exit(EXIT_FAILURE);\n    }\n\n    // Process has exited\n    printf("Process %d has exited\n", pid);\n    close(pidfd);\n    return 0;\n}