// main.c\n   #include <windows.h>\n   #include <stdio.h>\n\n   typedef int (__stdcall *Add2Func)(int);\n   typedef int (__stdcall *MulFunc)(int, int);\n\n   int main() {\n       HMODULE hDll = LoadLibrary("mydll.dll");\n       if (hDll) {\n           Add2Func add2 = (Add2Func)GetProcAddress(hDll, "add2");\n           MulFunc mul = (MulFunc)GetProcAddress(hDll, "mul");\n\n           if (add2 && mul) {\n               printf("add2(3): %d\n", add2(3));\n               printf("mul(4, 5): %d\n", mul(4, 5));\n           } else {\n               printf("Failed to get function addresses\n");\n           }\n\n           FreeLibrary(hDll);\n       } else {\n           printf("Failed to load DLL\n");\n       }\n\n       return 0;\n   }