<?php\n$conn = new mysqli('localhost', 'username', 'password', 'db_results');\n\nif($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$city_id = intval($_POST['city_id']);\n$area_id = intval($_POST['area_id']);\n$sql = "SELECT result_title FROM tbl_results WHERE result_city = $city_id AND result_area = $area_id";\n$result = $conn->query($sql);\n\n$results = "<h3>Results:</h3><ul>";\nwhile($row = $result->fetch_assoc()) {\n    $results .= "<li>{$row['result_title']}</li>";\n}\n$results .= "</ul>";\n\necho $results;\n$conn->close();\n?>