<?php\n// Encoding the text for the URL\n$text = "&#119;";  // This is the HTML entity for 'w'\n$encodedText = urlencode($text);\n\n// URL with the encoded text\n$url = "http://example.com/?format=text&type=htmldecode&text=" . $encodedText;\n\n// Output the URL\necho $url;\n\n// Assuming you have received the GET request\nif (isset($_GET['text'])) {\n    $receivedText = $_GET['text'];\n    // Decode the HTML entities\n    $decodedText = html_entity_decode($receivedText, ENT_QUOTES, 'UTF-8');\n    echo $decodedText; // Outputs: w\n}\n?>