<?php\n$myArray = array('apple', 'orange', 'grape');\n\ntry {\n    // Establish a connection to the database\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare the SQL statement\n    $stmt = $pdo->prepare("INSERT INTO `Fruits` (`FruitName`) VALUES (:fruitName)");\n\n    // Begin the transaction\n    $pdo->beginTransaction();\n\n    // Loop through the array and execute the query with prepared statements\n    foreach ($myArray as $arrayFruit) {\n        $stmt->execute([':fruitName' => $arrayFruit]);\n    }\n\n    // Commit the transaction\n    $pdo->commit();\n\n} catch (PDOException $e) {\n    // Rollback the transaction if an error occurs\n    $pdo->rollBack();\n    echo "Error: " . $e->getMessage();\n}\n?>