<?php \n$display_categories = array( 4, 7, 8, 9, 21, 1); \nforeach ( $display_categories as $category ) { ?>\n    <div>\n        <?php echo get_cat_name($category); ?> // Display the category name\n        <!-- Add any other category-related content here -->\n    </div>\n<?php } \n\n// Reset post data to clear the category query\nwp_reset_postdata(); \n\n// Create a new WP_Query to fetch posts ordered by date\n$q = new WP_Query( array(\n    'posts_per_page' => 15,\n    'orderby'        => 'date',\n    'order'          => 'DESC'\n) );\n\nif ( $q->have_posts() ) : \n    while ( $q->have_posts() ) : $q->the_post(); ?>\n        <div>\n            <h2><?php the_title(); ?></h2>\n            <!-- Add any other post-related content here -->\n        </div>\n    <?php endwhile; \n    wp_reset_postdata(); \nendif; \n?>