<?php\n// Encryption Function\nfunction encrypt($data, $encryption_key) {\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-cbc'));\n    $encrypted = openssl_encrypt($data, 'aes-256-cbc', $encryption_key, 0, $iv);\n    return base64_encode($encrypted . '::' . $iv);\n}\n\n// Decryption Function\nfunction decrypt($data, $encryption_key) {\n    list($encrypted_data, $iv) = explode('::', base64_decode($data), 2);\n    return openssl_decrypt($encrypted_data, 'aes-256-cbc', $encryption_key, 0, $iv);\n}\n\n// Example usage\n$encryption_key = 'your_encryption_key_here';\n$bank_info = '123456789'; // Example bank info\n\n$encrypted_bank_info = encrypt($bank_info, $encryption_key);\n$decrypted_bank_info = decrypt($encrypted_bank_info, $encryption_key);\n\necho "Encrypted: " . $encrypted_bank_info . "\n";\necho "Decrypted: " . $decrypted_bank_info . "\n";\n?>