class IndexController extends Zend_Controller_Action\n   {\n       public function init()\n       {\n           // Get the format from the request\n           $format = $this->_getParam('format', 'xhtml');\n\n           // Set the appropriate view script path based on the format\n           if ($format == 'wml') {\n               $this->_helper->viewRenderer->setViewSuffix('wml.phtml');\n           } else {\n               $this->_helper->viewRenderer->setViewSuffix('xhtml.phtml');\n           }\n       }\n\n       public function action()\n       {\n           // Your action code here\n       }\n   }