<?php\nfunction delphiTColorToRGB($tColor) {\n    $red = $tColor & 0xFF;\n    $green = ($tColor >> 8) & 0xFF;\n    $blue = ($tColor >> 16) & 0xFF;\n    return array('red' => $red, 'green' => $green, 'blue' => $blue);\n}\n\n// Example usage:\n$colors = [8388608, 128, 12632256];\nforeach ($colors as $tColor) {\n    $rgb = delphiTColorToRGB($tColor);\n    echo "TColor $tColor -> RGB(" . implode(", ", $rgb) . ")\n";\n}\n?>