<?php\n// Get the subdomain from the HTTP host\n$host = $_SERVER['HTTP_HOST'];\n$subdomain = explode('.', $host)[0];\n\n// Load the WSDL template\n$template = file_get_contents('wsdl_template.xml');\n\n// Replace the placeholder with the actual subdomain\n$wsdl = str_replace('{{subdomain}}', $subdomain, $template);\n\n// Serve the WSDL content with appropriate headers\nheader('Content-Type: application/xml');\necho $wsdl;\n?>