<?php\n// Set the timezone to EST\ndate_default_timezone_set('America/New_York');\n\n// Assume $datetime_from_db is the datetime value fetched from the database\n$datetime_from_db = '2023-10-15 12:00:00'; // Example datetime\n\n// Create a DateTime object from the fetched datetime\n$date = new DateTime($datetime_from_db, new DateTimeZone('UTC'));\n\n// Convert the datetime to EST\n$date->setTimezone(new DateTimeZone('America/New_York'));\n\n// Format the datetime as needed\n$formatted_date = $date->format('Y-m-d H:i:s');\n\necho $formatted_date;\n?>