<?php\nfunction add_target_blank($content) {\n    // Define the pattern to match external links\n    $pattern = '/<a\s+([^>]*?href=["\'](https?:\/\/.*?)[\'"][^>]*?)>/i';\n    \n    // Define the replacement pattern to add target="_blank"\n    $replacement = '<a $1 target="_blank">';\n    \n    // Perform the regex replacement\n    $content = preg_replace($pattern, $replacement, $content);\n    \n    return $content;\n}\n\n// Example usage\n$htmlContent = '<a href="http://example.com">External Link</a> <a href="/internal">Internal Link</a>';\n$modifiedContent = add_target_blank($htmlContent);\n\necho $modifiedContent;\n?>