require_once('lib/nusoap.php');\n\n$client = new nusoap_client("my.wsdl", true);\n\n// Define namespaces\n$ns1 = "http://domain.tld/namespace1";\n$ns2 = "http://domain.tld/namespace2";\n\n// Create the complex type for Person\n$person = new soapval(\n    'Person',\n    "ns2:Person",\n    array(\n        new soapval('FirstName', "ns2:FirstName", 'Thomas')\n    ),\n    $ns2\n);\n\n// Create the main operation parameter\n$params = new soapval(\n    'myOperation',\n    "ns1:myOperation",\n    array(\n        $person,\n        new soapval('Attribute', "ns1:Attribute", 'foo')\n    ),\n    $ns1\n);\n\n// Call the SOAP method\n$result = $client->call('myOperation', $params, $ns1, 'soapAction');\n\nif ($client->fault) {\n    echo "Fault: <pre>";\n    print_r($result);\n    echo "</pre>";\n} else {\n    $err = $client->getError();\n    if ($err) {\n        echo "Error: " . $err;\n    } else {\n        echo "Result: <pre>";\n        print_r($result);\n        echo "</pre>";\n    }\n}