<?php\n$string = "A/B/PA ID U/PA ID U/C/D";\n$pattern = '/\/?([^\/]+)/';\n\npreg_match_all($pattern, $string, $matches);\n\n$segments = $matches[1]; // Extracted segments\nforeach ($segments as $segment) {\n    if (preg_match('/^PA ID U$/', $segment)) {\n        echo "Matched: ($segment) ";\n    } else {\n        echo "Unmatched: ($segment) ";\n    }\n}\n?>