<?php\n// Path to the directory where images will be stored\ndefine('IMAGE_DIR', 'path/to/image/directory/');\n\n// Function to generate an image\nfunction createImage($filename) {\n    // Create a blank image\n    $width = 100;\n    $height = 100;\n    $image = imagecreatetruecolor($width, $height);\n    \n    // Set background color\n    $backgroundColor = imagecolorallocate($image, 255, 255, 255); // white\n    imagefill($image, 0, 0, $backgroundColor);\n    \n    // Add some text or graphics to the image\n    $textColor = imagecolorallocate($image, 0, 0, 0); // black\n    imagestring($image, 5, 10, 10, 'Sample Text', $textColor);\n    \n    // Save the image to the server\n    $filePath = IMAGE_DIR . $filename;\n    imagejpeg($image, $filePath);\n    \n    // Free memory\n    imagedestroy($image);\n    \n    return $filePath;\n}\n\n// Get the requested image file name\n$requestedImage = basename($_GET['image']);\n\n// Path to the requested image\n$imagePath = IMAGE_DIR . $requestedImage;\n\n// Check if the image exists\nif (!file_exists($imagePath)) {\n    // If the image doesn't exist, create it\n    $imagePath = createImage($requestedImage);\n}\n\n// Serve the image\nheader('Content-Type: image/jpeg');\nreadfile($imagePath);\nexit;\n?>