<?php\n$names = ["Alice", "Bob", "Charlie"];\n$images = ["alice.jpg", "bob.jpg", "charlie.jpg"];\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $selected = $_POST['selected'] ?? [];\n\n    $filteredNames = [];\n    $filteredImages = [];\n\n    foreach ($selected as $index) {\n        $filteredNames[] = $names[$index];\n        $filteredImages[] = $images[$index];\n    }\n\n    // Now you have the filtered arrays\n    echo "Selected Names: <pre>" . print_r($filteredNames, true) . "</pre>";\n    echo "Selected Images: <pre>" . print_r($filteredImages, true) . "</pre>";\n}\n?>