function recursiveSortFilesystem(&$filesystem) {\n    // Sort directories by their keys\n    ksort($filesystem);\n\n    foreach ($filesystem as &$value) {\n        if (is_array($value)) {\n            // If the value is an array, recursively sort it\n            recursiveSortFilesystem($value);\n        } else {\n            // If the value is not an array, sort the array\n            sort($value);\n        }\n    }\n}\n\n// Example usage\n$filesystem = [\n    'dir1' => [\n        'dir2' => [\n            'dir3' => [\n                'dir4' => [\n                    'file1.php',\n                    'abc.php'\n                ]\n            ],\n            'file2.php',\n            'abc.php'\n        ]\n    ],\n    'abc' => [\n        'abc' => [\n            'abc' => [\n                'file5.php'\n            ]\n        ]\n    ]\n];\n\nrecursiveSortFilesystem($filesystem);\nprint_r($filesystem);