$author = $_SESSION['username'];\n$subject = $_POST['subject'];\n$body = $_POST['body'];\n$branched = $_POST['branched'];\n$time = time();\n$branchedFrom = $_POST['parent'];\n$id = $_POST['parent'];\n$next = 0;\n$previous = 0;\n$branchedTo = 0;\n\n// Establish a connection to the MySQL database\n$mysqli = new mysqli('host', 'username', 'password', 'database');\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare the SQL statement\n$stmt = $mysqli->prepare("INSERT INTO offtopic (author, subject, body, time, next, previous, branchedFrom, branchedTo) VALUES (?, ?, ?, ?, ?, ?, ?, ?)");\n\nif ($stmt === false) {\n    die("Prepare failed: " . $mysqli->error);\n}\n\n// Bind the parameters\n$stmt->bind_param('sssiisis', $author, $subject, $body, $time, $next, $previous, $branchedFrom, $branchedTo);\n\n// Execute the statement\nif (!$stmt->execute()) {\n    die("Execute failed: " . $stmt->error);\n}\n\n// Close the statement and connection\n$stmt->close();\n$mysqli->close();