function filetime_to_unix_timestamp($filetime_hex) {\n    // Convert the hex string to a 64-bit integer\n    $filetime = hexdec($filetime_hex);\n\n    // Define the number of 100-nanosecond intervals between \n    // January 1, 1601 and January 1, 1970\n    $intervals = 116444736000000000;\n\n    // Subtract the intervals to get the Unix timestamp in 100-nanosecond intervals\n    $filetime -= $intervals;\n\n    // Convert to seconds\n    $timestamp = $filetime / 10000000;\n\n    return $timestamp;\n}\n\n// Example usage\n$filetime_hex = '01C99857D147F360';\necho date('Y-m-d H:i:s', filetime_to_unix_timestamp($filetime_hex));