class User {\n         private $id;\n         private $name;\n\n         public function __construct(int $id, string $name) {\n             $this->id = $id;\n             $this->name = $name;\n         }\n\n         public function getId(): int {\n             return $this->id;\n         }\n\n         public function getName(): string {\n             return $this->name;\n         }\n     }\n\n     function getUserById(int $id): User {\n         // Function implementation\n         return new User($id, "John Doe");\n     }