function timeDiff($firstTime, $lastTime) {\n    // Split the input times into minutes and seconds\n    list($firstMinutes, $firstSeconds) = explode(':', $firstTime);\n    list($lastMinutes, $lastSeconds) = explode(':', $lastTime);\n\n    // Convert both times to total seconds\n    $firstTotalSeconds = $firstMinutes * 60 + $firstSeconds;\n    $lastTotalSeconds = $lastMinutes * 60 + $lastSeconds;\n\n    // Calculate the difference in seconds\n    $timeDiffSeconds = $lastTotalSeconds - $firstTotalSeconds;\n\n    // Calculate the difference in minutes and seconds\n    $diffMinutes = floor($timeDiffSeconds / 60);\n    $diffSeconds = $timeDiffSeconds % 60;\n\n    // Format and return the result as "minutes:seconds"\n    return sprintf('%d:%02d', $diffMinutes, $diffSeconds);\n}\n\n// Example usage\necho timeDiff('1:07', '3:01'); // Outputs "1:54"