function resetAutoIncrement() {\n    $query = "SET @max_id = (SELECT MAX(member_id) FROM members)";\n    mysql_query($query) or showError("unable to query database for user information");\n\n    $query = "SET @new_ai = IFNULL(@max_id, 0) + 1";\n    mysql_query($query) or showError("unable to query database for user information");\n\n    $query = "SET @sql = CONCAT('ALTER TABLE members AUTO_INCREMENT = ', @new_ai)";\n    mysql_query($query) or showError("unable to query database for user information");\n\n    $query = "PREPARE stmt FROM @sql";\n    mysql_query($query) or showError("unable to query database for user information");\n\n    $query = "EXECUTE stmt";\n    mysql_query($query) or showError("unable to query database for user information");\n\n    $query = "DEALLOCATE PREPARE stmt";\n    mysql_query($query) or showError("unable to query database for user information");\n}\n\nfunction getLatestID() {\n    resetAutoIncrement();\n    $query = "SELECT max(member_id) FROM members";\n    $result = @mysql_query($query) or showError("unable to query database for user information");\n    if (!($record = mysql_fetch_array($result))) return null;\n    return $record[0];\n}