<?php\n$servername = "localhost";\n$username = "yourusername";\n$password = "yourpassword";\n$dbname = "yourdatabase";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$files = glob("path/to/files/ab*.html");\n\nforeach ($files as $file) {\n    $content = file_get_contents($file);\n\n    preg_match('/<div id="abEntryTitleText">(.*?)<\/div>/', $content, $title);\n    preg_match('/<div class="abEntryParagraph">(.*?)<\/div>/', $content, $paragraph);\n    preg_match('/<div class="abEntryDocumentNumber">(.*?)<\/div>/', $content, $docNumber);\n\n    $title = $conn->real_escape_string($title[1]);\n    $paragraph = $conn->real_escape_string($paragraph[1]);\n    $docNumber = $conn->real_escape_string($docNumber[1]);\n\n    $sql = "INSERT INTO your_table_name (title, content, lastupdated, related, visible, sortorder, userid, views, posvotes, negvotes, score, emailed, detectrelated, metakeywords, metadescription, startdate, enableexpiry, expirydate, featured, workflowstatus)\n            VALUES ('$title', '$paragraph', CURDATE(), '1', '1', '0', '1', '0', '1', '0', NULL, NULL, '1', NULL, '$docNumber', CURDATE(), '0', NULL, '0', 'auto_approved')";\n\n    if ($conn->query($sql) === TRUE) {\n        echo "New record created successfully for file: $file\n";\n    } else {\n        echo "Error: " . $sql . "\n" . $conn->error;\n    }\n}\n\n$conn->close();\n?>