<?php\n$conn = new mysqli('localhost', 'username', 'password', 'db_results');\n\nif($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$city_id = intval($_POST['city_id']);\n$sql = "SELECT area_id, area_name FROM tbl_area WHERE area_city = $city_id";\n$result = $conn->query($sql);\n\n$options = "<option value=''>Choose Area</option>";\nwhile($row = $result->fetch_assoc()) {\n    $options .= "<option value='{$row['area_id']}'>{$row['area_name']}</option>";\n}\n\necho $options;\n$conn->close();\n?>