<?php\n$thisXml = 'xml declaration stuff';\n\nfunction getThisXML($thisXml) {\n    // Assuming you have a database connection set up\n    for ($i = 1; $i < 5; $i++) {\n        $query = "SELECT * FROM your_table WHERE condition = $i"; // Example query\n\n        $theQuery = mysql_query($query);\n        while ($mysqlQuery = mysql_fetch_array($theQuery)) {\n            $thisXml .= 'add the xml';\n        }\n        $thisXml .= 'close the last element';\n    }\n    return $thisXml;\n}\n\n$thisXml = getThisXML($thisXml);\necho $thisXml;\n?>