<?php\nclass Person {\n    private $id;\n    private $name;\n    private $email;\n\n    public function __construct($id, $name, $email) {\n        $this->id = $id;\n        $this->name = $name;\n        $this->email = $email;\n    }\n\n    // Add getters and setters as needed\n}\n\n// Database connection details\n$host = 'your_host';\n$dbname = 'your_db';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->query('SELECT id, name, email FROM persons');\n    $persons = [];\n\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        $persons[] = new Person($row['id'], $row['name'], $row['email']);\n    }\n\n    // Display the list of persons\n    foreach ($persons as $person) {\n        // Assuming you have getters defined in your Person class\n        echo 'ID: ' . $person->getId() . '<br>';\n        echo 'Name: ' . $person->getName() . '<br>';\n        echo 'Email: ' . $person->getEmail() . '<br><br>';\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>