class QueryFailedException extends Exception {}\n\ntry {\n    if (!$this->connection[0]->query($this->query)) {\n        throw new QueryFailedException($this->connection[0]->error, 123);\n    }\n} catch (QueryFailedException $e) {\n    if ($e->getCode() == 123) {\n        // Handle specific error code 123\n        echo "Specific handling for error code 123: " . $e->getMessage();\n    } else {\n        // General error handling\n        echo "Query failed: " . $e->getMessage();\n    }\n}