<?php\n    require 'vendor/autoload.php';\n\n    // Set up the Google Client\n    $client = new Google_Client();\n    $client->setAuthConfig('path/to/credentials.json');\n    $client->setScopes(Google_Service_Gmail::GMAIL_READONLY);\n\n    // Redirect to OAuth 2.0 authorization if needed\n    if (!isset($_GET['code'])) {\n        $auth_url = $client->createAuthUrl();\n        header('Location: ' . filter_var($auth_url, FILTER_SANITIZE_URL));\n    } else {\n        $client->authenticate($_GET['code']);\n        $_SESSION['access_token'] = $client->getAccessToken();\n    }\n\n    // Set the access token\n    if (isset($_SESSION['access_token']) && $_SESSION['access_token']) {\n        $client->setAccessToken($_SESSION['access_token']);\n\n        // Create Gmail service instance\n        $service = new Google_Service_Gmail($client);\n\n        // Get the list of messages\n        $messages = $service->users_messages->listUsersMessages('me', ['q' => '']);\n\n        foreach ($messages->getMessages() as $message) {\n            $msg = $service->users_messages->get('me', $message->getId());\n            printf('Message ID: %s, Snippet: %s' . PHP_EOL, $msg->getId(), $msg->getSnippet());\n        }\n    }\n    ?>