<?php\n   require_once('vendor/autoload.php');\n\n   use setasign\Fpdi\Fpdi;\n   use setasign\Fpdi\PdfParser\StreamReader;\n\n   // Path to the PDF\n   $pdfFile = 'path/to/your/pdf_template.pdf';\n\n   // Initialize FPDI\n   $pdf = new Fpdi();\n   $pageCount = $pdf->setSourceFile($pdfFile);\n\n   // Iterate through all pages\n   for ($pageNo = 1; $pageNo <= $pageCount; $pageNo++) {\n       // Import a page\n       $templateId = $pdf->importPage($pageNo);\n\n       // Get the size of the imported page\n       $size = $pdf->getTemplateSize($templateId);\n\n       // Add a page with the same size\n       $pdf->AddPage($size['orientation'], array($size['width'], $size['height']));\n\n       // Use the imported page\n       $pdf->useTemplate($templateId);\n\n       // Get the fields and their coordinates\n       // This is a simplified example, real extraction will depend on the structure of the PDF\n       $fields = $pdf->getParser()->getPage($pageNo)->getContentStreamData();\n       foreach ($fields as $field) {\n           // Assuming you have a way to identify fields and get coordinates\n           // This part will need custom logic based on your PDF's structure\n           // Here, we just print out raw data for demonstration\n           echo 'Field data: ' . print_r($field, true) . "\n";\n       }\n   }\n   ?>