<?php\ninclude("run_connect.php"); // this connects to the database, this works\n\n// Create a connection object\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Escape the input to prevent SQL injection\n$first = $conn->real_escape_string($_POST['first']);\n\n$step1 = "INSERT INTO tblHurray (ID, first) VALUES (NULL, '$first')";\nif ($conn->query($step1) === TRUE) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $step1 . "<br>" . $conn->error;\n}\n\necho "<b><center>Database Output</center></b><br><br>";\n\n$step2 = $conn->query("SELECT * FROM tblHurray");\n\nif ($step2->num_rows > 0) {\n    while($row = $step2->fetch_assoc()) {\n        echo $row["ID"] . ": " . $row["first"] . "<br />";\n    }\n} else {\n    echo "0 results";\n}\n\n$conn->close();\n?>