// Fetch the global version/timestamp\n$global_version = file_get_contents('cache_version.txt');\n\n// Fetch the session version\n$session_version = isset($_SESSION['cache_version']) ? $_SESSION['cache_version'] : 0;\n\nif ($session_version != $global_version) {\n    // Invalidate session cache\n    unset($_SESSION['cached_productIds']);\n    // Update session version\n    $_SESSION['cache_version'] = $global_version;\n}\n\n// Now you can proceed to use the session cache or update it if needed\nif (!isset($_SESSION['cached_productIds'])) {\n    $_SESSION['cached_productIds'] = getProductIdsByCategory($categoryId);\n}