function isMobile() {\n       $userAgent = $_SERVER['HTTP_USER_AGENT'];\n       $mobileAgents = ['Android', 'iPhone', 'iPod', 'BlackBerry', 'Opera Mini', 'IEMobile'];\n\n       foreach ($mobileAgents as $agent) {\n           if (strpos($userAgent, $agent) !== false) {\n               return true;\n           }\n       }\n       return false;\n   }\n\n   if (isMobile()) {\n       header("Location: http://mobile.domain.com");\n       exit;\n   }