function get_newest_tabs() {\n    // Assuming you have your database connection stored in $db_open\n    $db_open = new mysqli('hostname', 'username', 'password', 'database');\n\n    if ($db_open->connect_error) {\n        die("Connection failed: " . $db_open->connect_error);\n    }\n\n    $sql = "SELECT \n                tabs.tab_id, \n                tabs.tab_version, \n                tabs.number_of_hits, \n                artist.artist_name, \n                users.user_alias, \n                songs.song_name, \n                tabs.time_added\n            FROM \n                tabs\n            JOIN \n                users ON tabs.user_id = users.user_id\n            JOIN \n                songs ON tabs.song_id = songs.song_id\n            JOIN \n                artist ON songs.artist_id = artist.artist_id\n            ORDER BY \n                tabs.time_added DESC \n            LIMIT 15";\n\n    if ($result = $db_open->query($sql)) {\n        $tabs = array();\n        while ($row = $result->fetch_assoc()) {\n            $tabs[] = $row;\n        }\n        $result->free();\n    } else {\n        die("ERROR - newest tabs function: " . $db_open->error);\n    }\n\n    $db_open->close();\n    return $tabs;\n}