$mysqli = new mysqli("hostname", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("INSERT IGNORE INTO my_table (column1, column2, column3, column4) VALUES (?, ?, 1, NOW())");\n$stmt->bind_param("ii", $_SESSION['client']['id'], $_POST['id']);\n\nif ($stmt->execute()) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n$stmt->close();\n$mysqli->close();