<?php\nfunction overwriteLineInFile($filePath, $searchString, $newContent) {\n    // Read the file into an array\n    $fileLines = file($filePath, FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);\n    \n    // Loop through the lines to find the target line\n    foreach ($fileLines as $key => $line) {\n        if (strpos($line, $searchString) !== false) {\n            // Overwrite the line with the new content\n            $fileLines[$key] = $newContent;\n            break;\n        }\n    }\n    \n    // Write the modified lines back to the file\n    file_put_contents($filePath, implode(PHP_EOL, $fileLines) . PHP_EOL);\n}\n\n// Example usage\n$filePath = 'path/to/your/file.txt';\n$searchString = '@parsethis';\n$newContent = '@parsethis New content for the line';\n\noverwriteLineInFile($filePath, $searchString, $newContent);\n?>