class Application_Model_Action extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'actions';\n\n    public function fetchAllWithUsers()\n    {\n        // Create a select object\n        $select = $this->select()\n                       ->setIntegrityCheck(false)\n                       ->from(array('a' => 'actions'))\n                       ->join(array('u' => 'users'), 'a.user_id = u.id', array('u.*'));\n\n        // Fetch all rows with the join\n        return $this->fetchAll($select);\n    }\n}