// Example function to call API from Application 2\nfunction getItem($id)\n{\n    $token = 'your-api-token'; // Retrieve this from a secure source\n    $url = 'https://app1.example.com/api/items/' . $id;\n\n    $options = [\n        'http' => [\n            'header' => "Authorization: Bearer $token\r\n",\n            'method' => 'GET'\n        ]\n    ];\n\n    $context = stream_context_create($options);\n    $response = file_get_contents($url, false, $context);\n\n    if ($response === FALSE) {\n        // Handle error\n    }\n\n    return json_decode($response, true);\n}