$db_host = "localhost";\n$db_name = "showfinder";\n$username = "user";\n$password = "password";\n$dbconn = pg_connect("host=$db_host dbname=$db_name user=$username password=$password")\n    or die('Could not connect: ' . pg_last_error());\n\n// Check if table exists in the public schema\n$check_query = "SELECT to_regclass('public.sf_bands')";\n$check_result = pg_query($check_query) or die('Table check failed: ' . pg_last_error());\n$table_exists = pg_fetch_result($check_result, 0, 0);\n\nif ($table_exists) {\n    $query = 'SELECT * FROM sf_bands LIMIT 10';\n    $result = pg_query($query) or die('Query failed: ' . pg_last_error());\n\n    // Fetch and display results\n    while ($row = pg_fetch_assoc($result)) {\n        print_r($row);\n    }\n} else {\n    die('Table "sf_bands" does not exist in schema "public".');\n}\n\n// Free resultset\npg_free_result($check_result);\npg_free_result($result);\n\n// Closing connection\npg_close($dbconn);