$array = [];\n$iterations = 1000000;\n\n// Benchmark for $array[] = $value\n$start = microtime(true);\nfor ($i = 0; $i < $iterations; $i++) {\n    $array[] = $i;\n}\n$end = microtime(true);\necho "Time for \$array[] = value: " . ($end - $start) . " seconds\n";\n\n$array = [];\n\n// Benchmark for array_push($array, $value)\n$start = microtime(true);\nfor ($i = 0; $i < $iterations; $i++) {\n    array_push($array, $i);\n}\n$end = microtime(true);\necho "Time for array_push(\$array, value): " . ($end - $start) . " seconds\n";