function get_webpage_source($url) {\n    $ch = curl_init();\n\n    // Set the URL\n    curl_setopt($ch, CURLOPT_URL, $url);\n    // Return the transfer as a string\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    // Set User-Agent for compatibility\n    curl_setopt($ch, CURLOPT_USERAGENT, 'Mozilla/5.0 (X11; U; Linux i686; en-US; rv:1.9b5) Gecko/2008050509 Firefox/3.0b5');\n\n    // Execute the session\n    $output = curl_exec($ch);\n\n    // Check for errors\n    if (curl_errno($ch)) {\n        $error_msg = curl_error($ch);\n        curl_close($ch);\n        return 'Error: ' . $error_msg;\n    }\n\n    // Close the cURL session\n    curl_close($ch);\n\n    return $output;\n}\n\n// Example usage\n$url = "http://www.example.com";\n$source = get_webpage_source($url);\necho $source;