<?php\n// Define the email content\n$to = 'recipient@example.com';\n$subject = 'Contact Form Response';\n$message = 'This is the content of the email.';\n$headers = 'From: sender@example.com' . "\r\n" .\n           'Reply-To: sender@example.com' . "\r\n" .\n           'X-Mailer: PHP/' . phpversion();\n\n// Prepare the mail command\n$mailCommand = "echo -e \"Subject: $subject\n$headers\n\n$message\" | sendmail $to";\n\n// SSH command to execute the mail command on the remote server\n$sshCommand = "ssh user@client-server '$mailCommand'";\n\n// Execute the SSH command\n$output = shell_exec($sshCommand);\n\nif ($output === null) {\n    echo "Failed to send email.";\n} else {\n    echo "Email sent successfully.";\n}\n?>