function isAscii($filePath) {\n    $handle = fopen($filePath, "r");\n    if (!$handle) {\n        return false;\n    }\n    \n    while (($buffer = fread($handle, 512)) !== false) {\n        if (preg_match('/[^\x00-\x7F]/', $buffer)) {\n            fclose($handle);\n            return false; // Binary file detected\n        }\n    }\n    \n    fclose($handle);\n    return true; // ASCII file\n}\n\n// Usage example\n$filePath = 'path/to/your/file.txt';\nif (isAscii($filePath)) {\n    echo 'The file is ASCII.';\n} else {\n    echo 'The file is binary.';\n}