function checkFile($fileTmpPath, $allowedMimes = ["image/jpeg", "image/png", "application/pdf", "application/zip"]) {\n    $finfo = finfo_open(FILEINFO_MIME_TYPE);\n    $mimeType = finfo_file($finfo, $fileTmpPath);\n    finfo_close($finfo);\n\n    if (in_array($mimeType, $allowedMimes)) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\n// Usage example:\nif (checkFile($_FILES['uploadedFile']['tmp_name'])) {\n    // Process the file\n} else {\n    // Reject the file\n}