<?php\n// Set the timezone to Australia/Melbourne\n$timezone = new DateTimeZone('Australia/Melbourne');\n\n// Create a DateTime object with the desired timezone\n$date = new DateTime('now', $timezone);\n\n// Use IntlDateFormatter to format the date and get the timezone abbreviation\n$formatter = new IntlDateFormatter(\n    'en_US', \n    IntlDateFormatter::FULL, \n    IntlDateFormatter::FULL, \n    $timezone->getName(), \n    IntlDateFormatter::GREGORIAN, \n    'zzz'\n);\n\n$formattedDate = $formatter->format($date);\necho $formattedDate;  // This should return "AEST" when in standard time\n?>