function renderTemplate($template, $canContinue) {\n    if ($canContinue) {\n        // If the condition is true, replace the tags with the content\n        $template = str_replace(['{continue}', '{/continue}'], '', $template);\n    } else {\n        // If the condition is false, remove the entire block\n        $template = preg_replace('/{continue}.*?{\/continue}/s', '', $template);\n    }\n    return $template;\n}\n\n// Example usage\n$template = "This is the intro {continue}click to continue{/continue} and this is the rest.";\n$canContinue = true; // or false\necho renderTemplate($template, $canContinue);