<?php\n// Lock the record\n$sql = "UPDATE records SET locked_by = :user_id, lock_time = NOW() WHERE id = :id AND (locked_by IS NULL OR lock_time < NOW() - INTERVAL 1 HOUR)";\n$stmt = $pdo->prepare($sql);\n$stmt->execute(['user_id' => $userId, 'id' => $recordId]);\n\nif ($stmt->rowCount() === 0) {\n    echo "Record is currently locked by another user.";\n} else {\n    // Record successfully locked for editing\n}\n?>