<?php\ninclude('inc/connection.php');\n$conn = dbConnect();\n\n// Movie data\n$movieid = 1; // Example movie ID\n$title = "Movie Title";\n$year = 2022;\n$runtime = 120;\n$plot = "This is the plot of the movie.";\n\n// Arrays\n$genres = ['Action', 'Crime', 'Drama'];\n$actors = ['Actor One', 'Actor Two', 'Actor Three'];\n\n// Insert into movies table\n$sql = 'INSERT INTO movies (movieid, title, year, runtime, plot) VALUES (?, ?, ?, ?, ?)';\n$stmt = $conn->stmt_init();\nif ($stmt->prepare($sql)) {\n    $stmt->bind_param('isiis', $movieid, $title, $year, $runtime, $plot);\n    $stmt->execute();\n}\n\n// Insert genres into genres table\n$sql_genre = 'INSERT INTO genres (genre_name) VALUES (?)';\n$stmt_genre = $conn->stmt_init();\nif ($stmt_genre->prepare($sql_genre)) {\n    foreach ($genres as $genre) {\n        $stmt_genre->bind_param('s', $genre);\n        $stmt_genre->execute();\n        \n        // Get the genre ID\n        $genre_id = $stmt_genre->insert_id;\n        \n        // Insert into movie_genres join table\n        $sql_movie_genre = 'INSERT INTO movie_genres (movieid, genreid) VALUES (?, ?)';\n        $stmt_movie_genre = $conn->stmt_init();\n        if ($stmt_movie_genre->prepare($sql_movie_genre)) {\n            $stmt_movie_genre->bind_param('ii', $movieid, $genre_id);\n            $stmt_movie_genre->execute();\n        }\n    }\n}\n\n// Insert actors into actors table\n$sql_actor = 'INSERT INTO actors (actor_name) VALUES (?)';\n$stmt_actor = $conn->stmt_init();\nif ($stmt_actor->prepare($sql_actor)) {\n    foreach ($actors as $actor) {\n        $stmt_actor->bind_param('s', $actor);\n        $stmt_actor->execute();\n        \n        // Get the actor ID\n        $actor_id = $stmt_actor->insert_id;\n        \n        // Insert into movie_actors join table\n        $sql_movie_actor = 'INSERT INTO movie_actors (movieid, actorid) VALUES (?, ?)';\n        $stmt_movie_actor = $conn->stmt_init();\n        if ($stmt_movie_actor->prepare($sql_movie_actor)) {\n            $stmt_movie_actor->bind_param('ii', $movieid, $actor_id);\n            $stmt_movie_actor->execute();\n        }\n    }\n}\n\n$conn->close();\n?>