<?php\n/*\nPlugin Name: Custom Preview Link\nDescription: Customize the preview link for WordPress posts and pages.\nVersion: 1.0\nAuthor: Your Name\n*/\n\nadd_filter('preview_post_link', 'custom_preview_link', 10, 2);\n\nfunction custom_preview_link($preview_link, $post) {\n    // Define your custom preview links based on post ID or slug\n    $custom_previews = array(\n        'contact' => 'contact.php',\n        'about' => 'about.php',\n        // Add more pages here\n    );\n\n    // Get the post slug\n    $post_slug = $post->post_name;\n\n    // Check if the post slug exists in the custom previews array\n    if (array_key_exists($post_slug, $custom_previews)) {\n        // Modify the preview link\n        return home_url($custom_previews[$post_slug]);\n    }\n\n    // Return the default preview link if no custom link is found\n    return $preview_link;\n}\n?>